export const getAllPokenmon = (url) => {
    return new Promise(
        (resolve, reject) => {
            fetch(url)
            .then((res) => res.json())
            .then((data) => resolve(data));
        }
    )
}

export const getPokemon = (url)=>{
    return new Promise(
        (resolve, reject) => {
            fetch(url)
            .then((res) => res.json())
            .then((data) => {
                console.log(data);
                resolve(data);
            });
        }
    )
}
