  const [loading,setLoading] = useState(true);

  //pokemonAPI情報取得の呼び出し
  useEffect(() => {
    const fetchPokemonData = async () => {
      let res = await getAllPokenmon(initialURL);
      console.log(res);
    }
    fetchPokemonData();
  }, []);

  return (
    <div className="App">
    </div>
  );
}

export default App;
